let burgerMenu = document.querySelector('.burger');
let navMenu = document.querySelector('.navigation_burger');
let menuItem = document.querySelector('.nav_menu__item')





burgerMenu.addEventListener('click', () => {
    burgerMenu.classList.toggle('active')
    navMenu.classList.toggle('open')

})

document.addEventListener('click', (event) => {
    if (navMenu.classList.contains('open') && !event.target.classList.contains('nav_menu__item') && !event.target.classList.contains('burger')) {
        navMenu.classList.remove('open')
        burgerMenu.classList.remove('active')

    }
})

